window.addEventListener("load",function(){document.querySelectorAll('[data-name="de"] input').forEach(function(e){e.addEventListener("keyup",function(e){5<e.target.value.length&&(e.target.value=e.target.value.replace(/([\d]{5})-?([\d]{3})?/gi,"$1-$2"))})}),document.querySelectorAll('[data-name="ate"] input').forEach(function(e){e.addEventListener("keyup",function(e){5<e.target.value.length&&(e.target.value=e.target.value.replace(/([\d]{5})-?([\d]{3})?/gi,"$1-$2"))})}),document.querySelectorAll('[data-name="cep"] input').forEach(function(e){e.addEventListener("keyup",function(e){5<e.target.value.length&&(e.target.value=e.target.value.replace(/([\d]{5})-?([\d]{3})?/gi,"$1-$2"))})})});